import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [formData, setFormData] = useState({
    fever: 98.6,     // Temperature in Fahrenheit
    cough: 0,        // 0: No, 1: Mild, 2: Moderate, 3: Severe
    fatigue: 0,      // 0: No, 1: Mild, 2: Moderate, 3: Severe
    diffBreath: 0,   // Difficulty Breathing 0: No, 1: Mild, 2: Moderate, 3: Severe
    headache: 0,     // 0: No, 1: Mild, 2: Moderate, 3: Severe
    soreThroat: 0,   // 0: No, 1: Yes
    runnyNose: 0,    // 0: No, 1: Yes
    bodyPain: 0,     // 0: No, 1: Mild, 2: Moderate, 3: Severe
    lossSmell: 0,    // Loss of Smell/Taste 0: No, 1: Yes
    age: 0          // Age in years
  });

  const [prediction, setPrediction] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const severityOptions = [
    { value: 0, label: 'No' },
    { value: 1, label: 'Mild' },
    { value: 2, label: 'Moderate' },
    { value: 3, label: 'Severe' }
  ];

  const yesNoOptions = [
    { value: 0, label: 'No' },
    { value: 1, label: 'Yes' }
  ];

  const featureLabels = {
    fever: "Body Temperature (°F)",
    cough: "Cough",
    fatigue: "Fatigue",
    diffBreath: "Difficulty Breathing",
    headache: "Headache",
    soreThroat: "Sore Throat",
    runnyNose: "Runny Nose",
    bodyPain: "Body Pain",
    lossSmell: "Loss of Smell/Taste",
    age: "Age (years)"
  };

  const medicineMap = {
    0: "No medication needed - Rest and monitor symptoms",
    1: "Paracetamol",
    2: "Ibuprofen",
    3: "Antibiotics",
    4: "Antiviral medication",
    5: "Combination therapy"
  };

  const handleChange = (feature, value) => {
    setFormData(prev => ({
      ...prev,
      [feature]: Number(value)
    }));
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setPrediction('');

    try {
      const features = [
        formData.fever,
        formData.cough,
        formData.fatigue,
        formData.diffBreath,
        formData.headache,
        formData.soreThroat,
        formData.runnyNose,
        formData.bodyPain,
        formData.lossSmell,
        formData.age
      ];

      const response = await axios.post('http://localhost:5000/predict', { features });
      const medicineClass = response.data.prediction;
      const medicineName = medicineMap[medicineClass] || 'Unknown medication';
      setPrediction(`Recommended Treatment: ${medicineName}`);
    } catch (error) {
      if (error.response) {
        setError(error.response.data.error || 'An error occurred while making the prediction.');
      } else if (error.request) {
        setError('No response from server. Please check if the backend is running.');
      } else {
        setError('Error occurred. Please check your input.');
      }
    } finally {
      setLoading(false);
    }
  };

  const renderInput = (feature) => {
    if (feature === 'fever') {
      return (
        <input
          type="number"
          value={formData[feature]}
          onChange={(e) => handleChange(feature, e.target.value)}
          min="95"
          max="105"
          step="0.1"
        />
      );
    } else if (feature === 'age') {
      return (
        <input
          type="number"
          value={formData[feature]}
          onChange={(e) => handleChange(feature, e.target.value)}
          min="0"
          max="120"
          step="1"
        />
      );
    } else if (['soreThroat', 'runnyNose', 'lossSmell'].includes(feature)) {
      return (
        <select
          value={formData[feature]}
          onChange={(e) => handleChange(feature, e.target.value)}
        >
          {yesNoOptions.map(option => (
            <option key={option.value} value={option.value}>
              {option.label}
            </option>
          ))}
        </select>
      );
    } else {
      return (
        <select
          value={formData[feature]}
          onChange={(e) => handleChange(feature, e.target.value)}
        >
          {severityOptions.map(option => (
            <option key={option.value} value={option.value}>
              {option.label}
            </option>
          ))}
        </select>
      );
    }
  };

  return (
    <div className="App">
      <h1>Medicine Recommendation System</h1>
      <div className="form-container">
        <form onSubmit={handleSubmit}>
          {Object.entries(featureLabels).map(([feature, label]) => (
            <div key={feature} className="input-group">
              <label htmlFor={feature}>{label}:</label>
              {renderInput(feature)}
            </div>
          ))}
          <button 
            type="submit" 
            disabled={loading}
            className={loading ? 'loading' : ''}
          >
            {loading ? 'Analyzing Symptoms...' : 'Recommend Treatment'}
          </button>
        </form>
      </div>
      {prediction && <p className="prediction">{prediction}</p>}
      {error && <p className="error-message">{error}</p>}
    </div>
  );
}

export default App;
