import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [formData, setFormData] = useState({
    fever: 0,        // Temperature in Fahrenheit
    cough: 0,        // 0: No, 1: Mild, 2: Moderate, 3: Severe
    fatigue: 0,      // 0: No, 1: Mild, 2: Moderate, 3: Severe
    diffBreath: 0,   // Difficulty Breathing 0: No, 1: Mild, 2: Moderate, 3: Severe
    headache: 0,     // 0: No, 1: Mild, 2: Moderate, 3: Severe
    soreThroat: 0,   // 0: No, 1: Yes
    runnyNose: 0,    // 0: No, 1: Yes
    bodyPain: 0,     // 0: No, 1: Mild, 2: Moderate, 3: Severe
    lossSmell: 0,    // Loss of Smell/Taste 0: No, 1: Yes
    age: 0          // Age in years
  });

  const [prediction, setPrediction] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const featureLabels = {
    fever: "Body Temperature (°F)",
    cough: "Cough Severity (0: No, 1: Mild, 2: Moderate, 3: Severe)",
    fatigue: "Fatigue Level (0: No, 1: Mild, 2: Moderate, 3: Severe)",
    diffBreath: "Difficulty Breathing (0: No, 1: Mild, 2: Moderate, 3: Severe)",
    headache: "Headache Severity (0: No, 1: Mild, 2: Moderate, 3: Severe)",
    soreThroat: "Sore Throat (0: No, 1: Yes)",
    runnyNose: "Runny Nose (0: No, 1: Yes)",
    bodyPain: "Body Pain (0: No, 1: Mild, 2: Moderate, 3: Severe)",
    lossSmell: "Loss of Smell/Taste (0: No, 1: Yes)",
    age: "Age (years)"
  };

  const medicineMap = {
    0: "No medication needed - Rest and monitor symptoms",
    1: "Paracetamol",
    2: "Ibuprofen",
    3: "Antibiotics",
    4: "Antiviral medication",
    5: "Combination therapy"
  };

  const handleChange = (feature, value) => {
    setFormData(prev => ({
      ...prev,
      [feature]: Number(value)
    }));
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setPrediction('');

    try {
      // Convert form data to features array in the correct order
      const features = [
        formData.fever,
        formData.cough,
        formData.fatigue,
        formData.diffBreath,
        formData.headache,
        formData.soreThroat,
        formData.runnyNose,
        formData.bodyPain,
        formData.lossSmell,
        formData.age
      ];

      const response = await axios.post('http://localhost:5000/predict', { features });
      const medicineClass = response.data.prediction;
      const medicineName = medicineMap[medicineClass] || 'Unknown medication';
      setPrediction(`Recommended Treatment: ${medicineName}`);
    } catch (error) {
      if (error.response) {
        setError(error.response.data.error || 'An error occurred while making the prediction.');
      } else if (error.request) {
        setError('No response from server. Please check if the backend is running.');
      } else {
        setError('Error occurred. Please check your input.');
      }
    } finally {
      setLoading(false);
    }
  };

  const getInputConstraints = (feature) => {
    switch (feature) {
      case 'fever':
        return { min: 95, max: 105, step: 0.1 };
      case 'age':
        return { min: 0, max: 120, step: 1 };
      case 'cough':
      case 'fatigue':
      case 'diffBreath':
      case 'headache':
      case 'bodyPain':
        return { min: 0, max: 3, step: 1 };
      case 'soreThroat':
      case 'runnyNose':
      case 'lossSmell':
        return { min: 0, max: 1, step: 1 };
      default:
        return {};
    }
  };

  return (
    <div className="App">
      <h1>Medicine Recommendation System</h1>
      <div className="form-container">
        <form onSubmit={handleSubmit}>
          {Object.entries(featureLabels).map(([feature, label]) => (
            <div key={feature} className="input-group">
              <label htmlFor={feature}>{label}:</label>
              <input
                id={feature}
                type="number"
                value={formData[feature]}
                onChange={(e) => handleChange(feature, e.target.value)}
                {...getInputConstraints(feature)}
              />
            </div>
          ))}
          <button 
            type="submit" 
            disabled={loading}
            className={loading ? 'loading' : ''}
          >
            {loading ? 'Analyzing Symptoms...' : 'Recommend Treatment'}
          </button>
        </form>
      </div>
      {prediction && <p className="prediction">{prediction}</p>}
      {error && <p className="error-message">{error}</p>}
    </div>
  );
}

export default App;
