import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [formData, setFormData] = useState({
    ageGroup: 'Adult',  // Child, Adult, Senior
    itching: 0,         // 0: No, 1: Yes
    nodalSkinEruptions: 0, // 0: No, 1: Yes
    shivering: 0,       // 0: No, 1: Yes
    stomachPain: 0,     // 0: No, 1: Yes
    vomiting: 0,        // 0: No, 1: Yes
    chestPain: 0,       // 0: No, 1: Yes
    lossOfAppetite: 0,  // 0: No, 1: Yes
    yellowUrine: 0,     // 0: No, 1: Yes
    restlessness: 0,    // 0: No, 1: Yes
    excessiveHunger: 0, // 0: No, 1: Yes
    highFever: 0,       // 0: No, 1: Yes
    diarrhoea: 0,       // 0: No, 1: Yes
    redSpotsOverBody: 0, // 0: No, 1: Yes
    breathlessness: 0,  // 0: No, 1: Yes
    darkUrine: 0,       // 0: No, 1: Yes
    skinRash: 0,        // 0: No, 1: Yes
    continuousSneezing: 0, // 0: No, 1: Yes
    chills: 0,          // 0: No, 1: Yes
    ulcersOnTongue: 0,  // 0: No, 1: Yes
    cough: 0,           // 0: No, 1: Yes
    yellowishSkin: 0,   // 0: No, 1: Yes
    abdominalPain: 0,   // 0: No, 1: Yes
    weightLoss: 0,      // 0: No, 1: Yes
    irregularSugarLevel: 0, // 0: No, 1: Yes
    increasedAppetite: 0, // 0: No, 1: Yes
    headache: 0,        // 0: No, 1: Yes
    musclePain: 0,      // 0: No, 1: Yes
    runnyNose: 0,       // 0: No, 1: Yes
    fastHeartRate: 0,   // 0: No, 1: Yes
    severity: 'Mild',   // Mild, Moderate, Severe
    duration: 0,        // Duration in days
    allergies: 'No',    // Yes, No
    existingCondition: 'None' // None, Diabetes, Hypertension, Asthma
  });

  const [prediction, setPrediction] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const yesNoOptions = [
    { value: 0, label: 'No' },
    { value: 1, label: 'Yes' }
  ];

  const ageGroupOptions = [
    { value: 'Child', label: 'Child' },
    { value: 'Adult', label: 'Adult' },
    { value: 'Senior', label: 'Senior' }
  ];

  const severityOptions = [
    { value: 'Mild', label: 'Mild' },
    { value: 'Moderate', label: 'Moderate' },
    { value: 'Severe', label: 'Severe' }
  ];

  const allergiesOptions = [
    { value: 'Yes', label: 'Yes' },
    { value: 'No', label: 'No' }
  ];

  const existingConditionOptions = [
    { value: 'None', label: 'None' },
    { value: 'Diabetes', label: 'Diabetes' },
    { value: 'Hypertension', label: 'Hypertension' },
    { value: 'Asthma', label: 'Asthma' }
  ];

  const handleChange = (feature, value) => {
    setFormData(prev => ({
      ...prev,
      [feature]: value
    }));
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setPrediction('');

    try {
      // Check if all symptoms are "No"
      const hasSymptoms = Object.entries(formData)
        .filter(([key]) => key !== 'ageGroup' && key !== 'severity' && key !== 'duration' && key !== 'allergies' && key !== 'existingCondition')
        .some(([_, value]) => value === 1);

      if (!hasSymptoms) {
        setPrediction('No specific medicines recommended as no symptoms are present.');
        setLoading(false);
        return;
      }

      // Convert form data to features array in the correct order
      const features = [
        formData.ageGroup === 'Child' ? 0 : formData.ageGroup === 'Adult' ? 1 : 2, // Convert age group to numeric
        formData.itching,
        formData.nodalSkinEruptions,
        formData.shivering,
        formData.stomachPain,
        formData.vomiting,
        formData.chestPain,
        formData.lossOfAppetite,
        formData.yellowUrine,
        formData.restlessness,
        formData.excessiveHunger,
        formData.highFever,
        formData.diarrhoea,
        formData.redSpotsOverBody,
        formData.breathlessness,
        formData.darkUrine,
        formData.skinRash,
        formData.continuousSneezing,
        formData.chills,
        formData.ulcersOnTongue,
        formData.cough,
        formData.yellowishSkin,
        formData.abdominalPain,
        formData.weightLoss,
        formData.irregularSugarLevel,
        formData.increasedAppetite,
        formData.headache,
        formData.musclePain,
        formData.runnyNose,
        formData.fastHeartRate,
        formData.severity === 'Mild' ? 0 : formData.severity === 'Moderate' ? 1 : 2, // Convert severity to numeric
        formData.duration,
        formData.allergies === 'Yes' ? 1 : 0,
        formData.existingCondition === 'None' ? 0 : 
        formData.existingCondition === 'Diabetes' ? 1 :
        formData.existingCondition === 'Hypertension' ? 2 : 3 // Convert existing condition to numeric
      ];

      const response = await axios.post('http://localhost:5000/predict', { features });
      setPrediction(`Recommended Medicines: ${response.data.prediction}`);
    } catch (error) {
      if (error.response) {
        setError(error.response.data.error || 'An error occurred while making the prediction.');
      } else if (error.request) {
        setError('No response from server. Please check if the backend is running.');
      } else {
        setError('Error occurred. Please check your input.');
      }
    } finally {
      setLoading(false);
    }
  };

  const renderInput = (feature, label) => {
    if (feature === 'ageGroup') {
      return (
        <select
          value={formData[feature]}
          onChange={(e) => handleChange(feature, e.target.value)}
        >
          {ageGroupOptions.map(option => (
            <option key={option.value} value={option.value}>
              {option.label}
            </option>
          ))}
        </select>
      );
    } else if (feature === 'severity') {
      return (
        <select
          value={formData[feature]}
          onChange={(e) => handleChange(feature, e.target.value)}
        >
          {severityOptions.map(option => (
            <option key={option.value} value={option.value}>
              {option.label}
            </option>
          ))}
        </select>
      );
    } else if (feature === 'allergies') {
      return (
        <select
          value={formData[feature]}
          onChange={(e) => handleChange(feature, e.target.value)}
        >
          {allergiesOptions.map(option => (
            <option key={option.value} value={option.value}>
              {option.label}
            </option>
          ))}
        </select>
      );
    } else if (feature === 'existingCondition') {
      return (
        <select
          value={formData[feature]}
          onChange={(e) => handleChange(feature, e.target.value)}
        >
          {existingConditionOptions.map(option => (
            <option key={option.value} value={option.value}>
              {option.label}
            </option>
          ))}
        </select>
      );
    } else if (feature === 'duration') {
      return (
        <input
          type="number"
          value={formData[feature]}
          onChange={(e) => handleChange(feature, Number(e.target.value))}
          min="1"
          max="30"
          step="1"
        />
      );
    } else {
      return (
        <select
          value={formData[feature]}
          onChange={(e) => handleChange(feature, Number(e.target.value))}
        >
          {yesNoOptions.map(option => (
            <option key={option.value} value={option.value}>
              {option.label}
            </option>
          ))}
        </select>
      );
    }
  };

  return (
    <div className="App">
      <h1>Medicine Recommendation System</h1>
      <div className="form-container">
        <form onSubmit={handleSubmit}>
          {Object.entries(formData).map(([feature, value]) => (
            <div key={feature} className="input-group">
              <label htmlFor={feature}>
                {feature.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}:
              </label>
              {renderInput(feature)}
            </div>
          ))}
          <button 
            type="submit" 
            disabled={loading}
            className={loading ? 'loading' : ''}
          >
            {loading ? 'Analyzing Symptoms...' : 'Recommend Medicines'}
          </button>
        </form>
      </div>
      {prediction && <p className="prediction">{prediction}</p>}
      {error && <p className="error-message">{error}</p>}
    </div>
  );
}

export default App;
