import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [features, setFeatures] = useState(Array(10).fill(0));
  const [prediction, setPrediction] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [isBackendHealthy, setIsBackendHealthy] = useState(false);

  useEffect(() => {
    checkBackendHealth();
  }, []);

  const checkBackendHealth = async () => {
    try {
      const response = await axios.get('http://localhost:5000/health');
      setIsBackendHealthy(response.data.status === 'healthy' && response.data.model_loaded);
      if (!response.data.model_loaded) {
        setError('Model is not loaded. Please try again later.');
      }
    } catch (error) {
      setError('Cannot connect to the server. Please make sure the backend is running.');
      setIsBackendHealthy(false);
    }
  };

  const handleChange = (index, value) => {
    const updatedFeatures = [...features];
    updatedFeatures[index] = Number(value);
    setFeatures(updatedFeatures);
    setError(''); // Clear any previous errors
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setPrediction('');

    try {
      const response = await axios.post('http://localhost:5000/predict', { features });
      setPrediction(`Predicted Medicine: ${response.data.prediction}`);
    } catch (error) {
      if (error.response) {
        setError(error.response.data.error || 'An error occurred while making the prediction.');
      } else if (error.request) {
        setError('No response from server. Please check if the backend is running.');
      } else {
        setError('Error occurred. Please check your input.');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <h1>Medicine Prediction App</h1>
      {!isBackendHealthy && (
        <div className="error-message">
          {error}
        </div>
      )}
      <form onSubmit={handleSubmit}>
        {features.map((feature, index) => (
          <div key={index} className="input-group">
            <label>Feature {index + 1}:</label>
            <input
              type="number"
              value={feature}
              onChange={(e) => handleChange(index, e.target.value)}
              disabled={!isBackendHealthy}
            />
          </div>
        ))}
        <button 
          type="submit" 
          disabled={loading || !isBackendHealthy}
          className={loading ? 'loading' : ''}
        >
          {loading ? 'Predicting...' : 'Predict Medicine'}
        </button>
      </form>
      {prediction && <p className="prediction">{prediction}</p>}
      {error && <p className="error-message">{error}</p>}
    </div>
  );
}

export default App;
